// choose.js
var db = require("db")
var util = require("./util.js")

function base(req, res){
	tpl_val = {
		selected:{"about":"selected"},
		title: "Today, tommorow title."
 	}
	res.render("index", tpl_val)
}
function move(req, res){
	res.redirect("/")
}

function choose_get(req, res){
	tpl_val = {
		selected:{"choose":"selected"},
		title: "Today, tommorow title."
 	}
	db.achievements.read({site:6,achievement:"*"}, function(err, achievements){
		if(err) return next(err)
		tpl_val.list = achievements
		res.render("list", tpl_val)
	})
}

this.setup = function setup(app){
	app.get('/choose', util.need.auth, choose_get)
	app.get('/track', util.need.wip)
}
